import React, { useState } from "react";
import PropTypes from "prop-types";
import { useParams } from "react-router-dom";

const DetailProduct = ({ products }) => {
    // console.log(products)
    let { id } = useParams();
    const product = products.find((product) => product.id === id);
    const [, setValueInput] = useState({});
    const onHandChange = (e) => {
        const { name, value } = e.target;
        setValueInput({
            ...valueInput,
            [name]: value,
        });
    };
    return (
        <div>
            <div className="main-panel">
                <div className="card shadow ml-10">
                    <div className="card-body">
                        <div className="table-responsive">
                            <table className="table">
                                <thead>
                                    <tr>
                                        <th scope="col">Name</th>
                                        <th scope="col">image</th>
                                        <th scope="col">Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>
                                            <input defaultValue={product.name} className="form-control" name="name"></input>
                                        </td>
                                        <td>
                                            <img src={product.image} width="100"></img>
                                            <br></br>
                                            <br></br>
                                            <input className="form-control" defaultValue={product.image} border="0px"></input>
                                        </td>
                                        <td>
                                           
                                            <button className="btn btn-primary" onClick={onHandChange}>Update</button>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

DetailProduct.propTypes = {};

export default DetailProduct;
