import React, { useState } from "react";
import PropTypes from "prop-types";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

const ProductsManager = ({ products, onRemove ,onSearch}) => {
    console.log(products);
    const removeHandle = (id) => {
        onRemove(id);
    };
    const [valueInput, setValueInput] = useState({});
    const onHandChange = (e) => {
        const { name, value } = e.target;
        setValueInput({
            ...valueInput,
            [name]: value,
        });
    };

    const onHandSearch = data => {
        console.log(valueInput);

        // console.log(products)

        if (!data=="") {
            data.preventDefault();
            onSearch(valueInput)
        }
    };

    return (
        <div>
            <div className="main-panel">
                <nav className="navbar navbar-expand-lg navbar-transparent navbar-absolute fixed-top ">
                    <div className="container-fluid">
                        <div className="collapse navbar-collapse justify-content-end">
                            <form className="navbar-form" onSubmit={onHandSearch}>
                                <div className="input-group no-border">
                                    <input type="text" className="form-control" defaultValue={valueInput.name || ""} placeholder="Search..." name="name" onChange={onHandChange} />
                                    <button type="submit" className="btn btn-white btn-round btn-just-icon">
                                        <i className="material-icons">search</i>
                                        <div className="ripple-container" />
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </nav>
                <div className="card shadow ml-10">
                    {/* <div className="card-header card-header-primary"></div> */}
                    <div className="card-body">
                        <div className="table-responsive">
                            <table className="table">
                                <thead>
                                    <tr>
                                        <th scope="col">STT</th>
                                        <th scope="col">Name</th>
                                        <th scope="col">Image</th>
                                        <th scope="col">Price</th>
                                        <th scope="col">Status</th>
                                        <th scope="col">Detail</th>
                                        <th scope="col">Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {products.map(({ id, name, image, price,status, textAra }, index) => (
                                        <tr key={index}>
                                            <th scope="row">{index + 1}</th>
                                            <td>
                                                <Link to={"product/id/" + id}>{name}</Link>
                                            </td>
                                            <td>
                                                <img src={image} alt="" width="100" />
                                            </td>
                                            <td>{price}</td>
                                            <td>{status}</td>
                                            <td>{textAra}</td>
                                            <td>
                                                <button className="btn btn-primary" onClick={() => removeHandle(id)}>
                                                    Delete
                                                </button>
                                                <button className="btn btn-primary" onClick={() => removeHandle(id)}>
                                                    Edit
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

ProductsManager.propTypes = {};

export default ProductsManager;
