import React from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LayoutMain from '../pages/layouts/LayoutMain'
import LayoutAdmin from '../pages/layouts/LayoutAdmin'
//Admin
import Dashboard from '../pages/views/Admin/Dashboard'
import ProductsManager from '../pages/views/Admin/Products'
//Views
import About from '../pages/views/Main/About'
// import Home from '../pages/views/Main/Home'
import AddProduct from '../components/Admin/AddProduct';
import DetailProduct from '../components/Admin/DetailProduct';
import Product from '../components/Main/Product/index';
import Detail from '../pages/views/Main/Detail';
import Home from '../pages/views/Main/Home/index';
import Category from '../pages/views/Admin/Category';
import AddCategory from '../pages/views/Admin/AddCategory';




const Routers = ({ products, onRemove ,onAdd, onUpdate,category ,onAddCate}) => {

    const onHandleRemove = (id) => {
        onRemove(id);
    }
    const onHandAdd =(products)=>{
        onAdd(products);
    }
    const onHandleUpdate = (id, product) => {
        onUpdate(id, product);
    }
    const onHandAddCate = (category)=>{
        onAddCate(category);
    }

    return (
        <Router>
            <Switch>
                <Route path="/admin/:path?/:path?/:path?" exact>
                    <LayoutAdmin>
                        <Switch>
                            <Route path='/admin' exact render={(props) =>
                                <Dashboard {...props} products={products}/>}>
                            </Route>
                            <Route path='/admin/products' exact render={(props) =>
                                <ProductsManager {...props} products={products} onRemove={onHandleRemove} />
                            }>
                            </Route>

                            <Route path='/admin/product/add' exact render={(props) =>
                                <AddProduct  {...props} onAdd={onHandAdd} />}>
                            </Route>
                            <Route path='/admin/product/id/:id' exact render={(props) =>
                                <DetailProduct  {...props} products={products} onUpdate={onHandleUpdate}/>
                            }>
                            </Route>
                            {/* category */}
                            <Route path='/admin/category' exact render={(props) =>
                              <Category category={category} />}>
                            </Route>
                            <Route path='/admin/category/add' exact render={(props) =>
                                <AddCategory onAddCate={onHandAddCate}/>}>
                            </Route>
                        </Switch>
                    </LayoutAdmin>
                </Route>
                <Route path="/:path?/:path?/:path?" exact>
                    <LayoutMain>
                        <Switch>
                            <Route path='/' exact>
                               <Home/>
                            </Route>
                            <Route path='/product' >
                                <Product products={products}/>
                            </Route>
                            <Route path="/product_:id">
                               <Detail products={products}/>                         
                            </Route>
                            <Route path="/about" >
                                <About />
                            </Route>
                        </Switch>
                    </LayoutMain>
                </Route>
            </Switch>
        </Router>
    )
}
Routers.propTypes = {
}
export default Routers
