import React, { useState, useEffect } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from './dataFake';
import Routers from './routers';

import apiRequest from '../src/api/productApi'
// import {
//   BrowserRouter as Router,
//   Switch,
//   Route,
//   Link
// } from "react-router-dom";
import AddProduct from './components/Admin/AddProduct/index';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
function App() {

  const [products, setProducts] = useState([]);
  const [status, setStatus] = useState(false);
  const [color, setColor] = useState('green');
  const onHandleClick = () => {
    // setStatus(true);
    setColor('red');
  }
  // Xóa sản phẩm
  const onHandleRemove = async (id) => {
    try {
      const { data } = await apiRequest.remove(id);
      const newProducts = products.filter(product => product.id !== data.id);
      setProducts(newProducts);
    } catch (error) {
      console.log('failed to request API: ', error)
    }

  }
  // Danh sách sản phẩm
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();
  }, []);
  // Thêm sản phẩm
  const onHanleAdd= async (product) => {
    try {
      const { data } = await apiRequest.create(product);
      setProducts([
        ...products,
        data
      ])
    } catch (error) {
      console.log('failed to request API: ', error)
    }
  }
 const onHandSearch =(product)=>{
   const newProduct = products.filter(product=>(product.name.toLowerCase().indexOf(product.toLowerCase()))!==-1);
   setProducts(newProduct);
 }
  const onHandleUpdate = (id, product) => {
    const abc = {...product};
    const newProducts = products.map(pro => (pro.id == id ? {...product} : pro));
    localStorage.setItem('products', JSON.stringify(newProducts))
    setProducts(newProducts);
  }
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHanleAdd} onUpdate={onHandleUpdate} onSearch={onHandSearch}/>

    </div>
  )

}
export default App;
