import React from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LayoutMain from '../pages/layouts/LayoutMain'
import LayoutAdmin from '../pages/layouts/LayoutAdmin'
//Admin
import Dashboard from '../pages/views/Admin/Dashboard'
import ProductsManager from '../pages/views/Admin/Products'
//Views
import About from '../pages/views/Main/About'
// import Home from '../pages/views/Main/Home'
import AddProduct from '../components/Admin/AddProduct';
import DetailProduct from '../components/Admin/DetailProduct';
import Product from '../components/Main/Product/index';



const Routers = ({ products, onRemove ,onAdd, onUpdate }) => {

    const onHandleRemove = (id) => {
        onRemove(id)
    }
    const onHandAdd =(products)=>{
        onAdd(products)
    }
    const onHandleUpdate = (id, product) => {
        onUpdate(id, product);
    }
    return (
        <Router>
            <Switch>
                <Route path="/admin/" exact>
                    <LayoutAdmin>
                        <Switch>
                            <Route path='/admin' exact>
                                <Dashboard />
                            </Route>
                            <Route path='/admin/products'>
                                <ProductsManager products={products} onRemove={onHandleRemove} />                               
                            </Route>
                            <Route path='/admin/product/add'>
                                <AddProduct  onAdd={onHandAdd} />
                            </Route>
                            <Route path='/admin/product/id/:id'>
                                <DetailProduct products={products} onUpdate={onHandleUpdate}/>
                            </Route>
                        </Switch>
                    </LayoutAdmin>
                </Route>
                <Route path="/:path?" exact>
                    <LayoutMain>
                        <Switch>
                            <Route path='/' exact>
                                <Product products={products}/>
                            </Route>
                            <Route path='/product/detail/:id' exact>                               
                            </Route>
                            <Route path="/about">
                                <About />
                            </Route>
                        </Switch>
                    </LayoutMain>
                </Route>
            </Switch>
        </Router>
    )
}
Routers.propTypes = {
}
export default Routers
