import React, { useState } from "react";
import PropTypes from "prop-types";
import { useParams } from "react-router-dom";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

const DetailProduct = ({ onAdd, products, onUpdate }) => {
    console.log(products);
    const { id } = useParams();

    const product = id ? products.find((product) => product.id == id) : {};
    const [valueInput, setValueInput] = useState(product);
    const { handleSubmit, register, errors } = useForm();
    const onHandleChange = (e) => {
        const { name, value } = e.target;

        setValueInput({
            ...valueInput,
            [name]: value,
        });
    };
    const history = useHistory();
    const onHandleSubmit = (data) => {
        if (data) {
            if (!id) {
                onAdd(valueInput);
                history.push("/admin/products");
            } else {
                onUpdate(id, valueInput);
                history.push("/admin/products");
            }
        }
    };
    return (
        <div>
            <div className="main-panel">
                <div className="card shadow ml-10">
                    <div className="card-body">
                        <div className="table-responsive">
                            <form onSubmit={handleSubmit(onHandleSubmit)}>
                                <table className="table">
                                    <thead>
                                        <tr>
                                            <th scope="col">Name</th>
                                            <th scope="col">image</th>
                                            <th scope="col">Price</th>
                                            <th scope="col">Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td>
                                                <input
                                                    defaultValue={valueInput.name || ""}
                                                    ref={register({
                                                        required: true,
                                                    })}
                                                    className="form-control"
                                                    name="name"
                                                    type="text"
                                                ></input>
                                                {errors.name && errors.name.type === "required" && <span className="text-danger">Bạn không để trống</span>}
                                            </td>

                                            <td>
                                                <img src={product.image} width="100"></img>
                                                <br></br>
                                                <br></br>
                                                <input
                                                    className="form-control"
                                                    defaultValue={valueInput.image || ""}
                                                    ref={register({
                                                        required: true,
                                                    })}
                                                    border="0px"
                                                    type="text"
                                                    name="image"
                                                ></input>
                                                {errors.image && errors.image.type === "required" && <span className="text-danger">Bạn không để trống</span>}
                                            </td>
                                            <td>
                                                <input
                                                    ref={register({
                                                        required: true,
                                                    })}
                                                    className="form-control"
                                                    defaultValue={valueInput.price || ""}
                                                    name="price"
                                                ></input>{" "}
                                                {errors.price && errors.price.type === "required" && <span className="text-danger">Bạn không để trống</span>}
                                            </td>

                                            <td>
                                                <button className="btn btn-primary">Update</button>
                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

DetailProduct.propTypes = {};

export default DetailProduct;
