import React, { useState } from "react";
import PropTypes from "prop-types";
import { useForm } from "react-hook-form";

const AddProduct = ({ onAdd }) => {
    const {handleSubmit, register, errors } = useForm();
    const [valueInput, setValueInput] = useState({});
    const onHandChange = (e) => {
        const { name, value } = e.target;
        setValueInput({
            ...valueInput,
            [name]: value,
        });
    };
    const onHandSubmit = () => {
       
        onAdd(valueInput); 
        // e.preventDefault();
    };
    return (
        <div className="main-panel">
            <div  className="card shadow ml-10">
        <form action="" onSubmit={handleSubmit(onHandSubmit)}>
            <div className="form-group row ml-2 mt-5">
                <label htmlFor="" className="col-sm-2 col-form-label">
                    Name Product
                </label>
                <div className="col-sm-10">
                    <input
                        type="text"
                        className="form-control"
                        ref={register({
                            required: true,
                            minLength:3,
                            maxLength:15
                          })}
                        onChange={onHandChange}
                        name="name"
                    />
                </div>
                {errors.name && errors.name.type ==="required" && <span className='text-danger'>Bạn không để trống</span>}
                {errors.name && errors.name.type ==="minLength" && <span className='text-danger'>Bạn nhập ngắn hơn 3 kí tự</span>}
                {errors.name && errors.name.type ==="required" && <span className='text-danger'>Bạn nhập dài quá 15 kí tự</span>}
            </div>
            <div className="form-group row ml-2">
                <label htmlFor="" className="col-sm-2 col-form-label">
                    Image
                </label>
                <div className="col-sm-10">
                    <input type="text" className="form-control" onChange={onHandChange} name="image" />
                </div>
            </div>
            <div className="form-group row ml-2">
                <label htmlFor="" className="col-sm-2 col-form-label">
                    price
                </label>
                <div className="col-sm-10">
                    <input type="text" className="form-control" onChange={onHandChange} name="price"  
                    ref={register({
                            required: true,
                            pattern: {
                                value: /^\d{0,10tẽ}$/                                   
                              }
                          })}/>
                  {errors.price && errors.price.type ==="required" && <span className=''>Bạn không để trống</span>}   
                  {errors.price && errors.price.type ==="pattern" && <span className=''>Nhập từ 0 - 9</span>}      
                </div>
            </div>
            <div className="form-group form-check ml-4">
                <input type="radio" className="form-check-input" name="status" onChange={onHandChange} value="Còn hàng" />
                <label className="form-check-label" htmlFor="exampleCheck1">
                    Còn hàng
                </label>
            </div>
            <div className="form-group form-check ml-4">
                <input type="radio" className="form-check-input" name="status" onChange={onHandChange} value="Hết hàng" />
                <label className="form-check-label" htmlFor="exampleCheck1">
                    Hết hàng
                </label>
            </div>
            <div className="form-group ml-4">
                <label htmlFor="exampleFormControlTextarea1">Example textarea</label>
                <textarea className="form-control" rows={3} name="textAra" onChange={onHandChange} />
            </div>
            <button className="btn btn-success ml-4">Add Product</button>
        </form>
    </div>
    </div>
    
    )
}

AddProduct.propTypes = {

}

export default AddProduct
