        <div className="col-6">
          <img src="image" alt="" />
        </div>
        <div className="col-6">
          <a href><h4>Điện thoại </h4></a>
          <h4 id="colH4">{'{'}{'{'}number_format($row-&gt;price_km){'}'}{'}'} VNĐ</h4>
