          <i className="fas fa-check-circle"><span style={{paddingLeft: '15px'}}>Đổi trả miễn phí 30 ngày</span></i><br />
          <i className="fas fa-angle-double-right"><span style={{paddingLeft: '15px'}}>Phục vụ tận tình</span></i> <br />
          <button type="button" className="btn btn-outline-success">Mua ngay</button>
          <a href="{{route('Product.AddCart',['id'=>$row->id])}}">
            <button type="button" className="btn btn-outline-danger">Thêm vào giỏ hàng</button>
          </a>
        </div>
      </div>
        </div>
    )
}

