    // console.log(products)
    let {id} =useParams();
    const product = products.find(product =>product.id === id);
    const [valueInput, setValueInput] = useState({});
   
    
    const onHandChange = (e) => {
        const { name, value } = e.target;
        valueInput = value
    };
    const SaveNew =(da)=>{

    }
    
    return (
        <div className="main-panel">
            <div className="card shadow ml-10">
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table">
                        <thead>
                                <tr>                                   
                                    <th scope="col">Name</th>
                                    <th scope="col">image</th>
                                    <th scope="col">Action</th>
                                </tr>
                        </thead>
                        <tbody>
                            <th>
                                <input defaultValue={product.name} className="form-control"
                            ref={(data_product)=>{}}  onChange={onHandChange} name="name">
                                </input>
                            </th>
                            <th><img src={product.image} width="100"></img><br></br><br></br>
                            <input className="form-control" defaultValue={product.image} border="0px"></input>
                            </th>
                            <th> <button className="btn btn-primary" onClick={SaveNew}>Update</button></th>
                        </tbody>
            
            </table>
        </div>
        </div>
        </div>
        </div>
    )
}

