import React, { useState } from "react";
import PropTypes from "prop-types";
import { useParams } from "react-router-dom";


const DetailProduct = ({ onAdd , products, onUpdate }) => {
    console.log(products)
    const { id } = useParams();

    const product = id?products.find(product => product.id == id) : {};
    const [valueInput, setValueInput] = useState(product);

    const onHandleChange = (e) => {
        const { name, value } = e.target;
        
        setValueInput({
            ...valueInput,
            [name]: value
        })
    }
    const history = useHistory();
    const onHandleSubmit = data => {
        let message = "";
        if(data){
            if(!id) {
                onAdd(valueInput);
               
                
            }else{
                onUpdate(id, valueInput);
                message = "Update Product Successfully"
            }
            history.push({
                pathname: "/admin/products", 
                message: message
            });
        }
    }
    return (
        <div>
            <div className="main-panel">
                <div className="card shadow ml-10">
                    <div className="card-body">
                        <div className="table-responsive">
                            <table className="table">
                                <thead>
                                    <tr>
                                        <th scope="col">Name</th>
                                        <th scope="col">image</th>
                                        <th scope="col">Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>
                                            <input defaultValue={product.name} className="form-control" name="name" type="text"></input>
                                        </td>
                                        
                                        <td>
                                            <img src={product.image} width="100"></img>
                                            <br></br>
                                            <br></br>
                                            <input className="form-control" defaultValue={product.image} border="0px" type="text"></input>
                                        </td>
                                        <td>
                                           
                                            <button className="btn btn-primary" >Update</button>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

DetailProduct.propTypes = {};

export default DetailProduct;
